options.elementValueChanged:
console.log(context.panel.elements, context.panel.options.elements);
// // console.log(context.panel.data.series);

const fieldElement = context.panel.elements[0].value;

const deviceElements = context.panel.data.series[2].fields[2].values;
const locationElements = context.panel.data.series[2].fields[3].values;

context.panel.onOptionsChange({
  ...context.panel.options,
  elements: context.panel.elements.map((element) => {

    if (fieldElement == null) { 
      return element
    }

    if (element.id === "mac_address") { 
      return { ...element, value: deviceElements[fieldElement - 1] };
    }
    if (element.id === "location") { 
      return { ...element, value: locationElements[fieldElement - 1] };
    }
    return element;
  }),
});

